// Log de debug para ver la estructura completa de datos
console.log('🔍 Estructura completa de datos:', JSON.stringify(items, null, 2));
console.log('🔍 Primer item:', JSON.stringify(items[0], null, 2));

// Acceder a los datos correctamente desde el webhook
let data;
if (items[0].json.body) {
  // Datos vienen en body (típico de webhooks)
  data = items[0].json.body;
  console.log('📦 Datos desde body:', JSON.stringify(data, null, 2));
} else {
  // Datos vienen directamente
  data = items[0].json;
  console.log('📦 Datos directos:', JSON.stringify(data, null, 2));
}

// Validar que tengamos los datos necesarios
if (!data.playerName || data.score === undefined || !data.chatId) {
  console.error('❌ Faltan datos importantes:', {
    playerName: !!data.playerName,
    score: data.score !== undefined,
    chatId: !!data.chatId,
    datosRecibidos: data
  });
  return [{
    json: {
      error: 'Datos incompletos',
      receivedData: data,
      fullStructure: items[0]
    }
  }];
}

// Crear mensaje personalizado para Telegram
let message = `🎮 ¡${data.playerName} ha logrado ${data.score} puntos en KOKOK THE ROACH! 🪳\n\n`;

// Agregar emoji y mensaje según la puntuación
if (data.score >= 1000) {
  message += `🏆 ¡LEYENDA CUCARACHA! ¡Puntuación épica! 🏆\n`;
} else if (data.score >= 500) {
  message += `🔥 ¡INCREÍBLE PUNTUACIÓN! ¡Eres un maestro cucaracha! 🔥\n`;
} else if (data.score >= 300) {
  message += `⭐ ¡EXCELENTE! ¡Dominas el arte del combate! ⭐\n`;
} else if (data.score >= 200) {
  message += `💪 ¡MUY BUENO! ¡Sigue mejorando! 💪\n`;
} else if (data.score >= 100) {
  message += `👍 ¡BUEN TRABAJO! ¿Puedes llegar a 200? 👍\n`;
} else if (data.score >= 50) {
  message += `🚀 ¡BUEN INICIO! ¡La práctica hace al maestro! 🚀\n`;
} else {
  message += `💫 ¡SIGUE INTENTANDO! ¡Cada cucaracha empieza desde abajo! 💫\n`;
}

message += `\n💪 ¿Puedes superarlo? ¡Juega ahora y conviértete en la cucaracha más poderosa!\n\n#KokokTheRoach #CryptoGame #${data.playerName.replace(/[^a-zA-Z0-9]/g, '')}`;

// Generar estadísticas de rendimiento
let performanceLevel = 'Novato';
if (data.score >= 1000) performanceLevel = 'Leyenda';
else if (data.score >= 500) performanceLevel = 'Experto';
else if (data.score >= 300) performanceLevel = 'Avanzado';
else if (data.score >= 100) performanceLevel = 'Intermedio';

console.log('✅ Mensaje generado:', message);

// Devolver datos procesados
return [{
  json: {
    chatId: data.chatId,
    playerName: data.playerName,
    score: data.score,
    message: message,
    isManualShare: data.additionalData?.isManualShare || true,
    platform: data.additionalData?.platform || 'unknown',
    performanceLevel: performanceLevel,
    timestamp: data.timestamp,
    gameSession: data.gameSession
  }
}];
